mapper.update:
update board_table
set boardHits = boardHits+1
where id = #{id}

mapper.insert:
insert into board_table(
                         boardTitle
                       , boardWriter
                       , boardPass
                       , boardContents
                        )
values(
        #{boardTitle}
      , #{boardWriter}
      , #{boardPass}
      , #{boardContents}
      )

mapper.update:
UPDATE board_table
SET  boardTitle = #{boardTitle}
   , boardWriter = #{boardWriter}
   , boardPass = #{boardPass}
   , boardContents = #{boardContents}
WHERE 1=1
  AND id = #{id}

mapper.delete:
DELETE FROM board_table
WHERE 1=1
  AND id = #{id}
